    try {
      const res = await updateLesson(lesson._id, payload);
      setAllLessons((prev) => prev.map((l) => (l._id === lesson._id ? res.data : l)));
      notify("Lesson updated", "success");
    } catch (err) {
      notify("Failed to update lesson.", "error");
      console.error(err);
    }
  };

